<?php$route = '/ongithub/products/';$app->get($route, function ()  use ($app){	if(isset($_REQUEST['query'])){ $query = $_REQUEST['query']; } else { $query = '';}	$ObjectText = file_get_contents('https://raw.github.com/ongithub/products/gh-pages/products.json');	$ObjectResult = json_decode($ObjectText,true);	$ReturnObject = array();	foreach($ObjectResult as $Object){		$IncludeRecord = 1;		$id = $Object['id'];		$name = $Object['name'];		$description = $Object['description'];		$url = $Object['url'];		$image = $Object['image'];		$thumbnailUrl = $Object['thumbnailUrl'];		$keywords = $Object['keywords'];		$brand = $Object['brand'];		$color = $Object['color'];				$itemCondition = $Object['itemCondition'];		$manufacturer = $Object['manufacturer'];		$model = $Object['model'];		$sku = $Object['sku'];		$weight = $Object['weight'];		$width = $Object['width'];		$height = $Object['height'];		$depth = $Object['depth'];		if($query!=''){			$IncludeRecord = 0;			if(strpos(strtolower($name),strtolower($query)) === 0 || strpos(strtolower($description),strtolower($query)) === 0)				{				$IncludeRecord = 1;				}			}		if($IncludeRecord==1)			{								$F = array();			$F['id'] = $id;			$F['name'] = $name;			$F['description'] = $description;			$F['url'] = $url;			$F['image'] = $image;			$F['thumbnailUrl'] = $thumbnailUrl;			$F['keywords'] = $keywords;			$F['brand'] = $brand;			$F['color'] = $color;			$F['itemCondition'] = $itemCondition;			$F['manufacturer'] = $manufacturer;			$F['model'] = $model;			$F['sku'] = $sku;			$F['weight'] = $weight;			$F['width'] = $width;			$F['height'] = $height;			$F['depth'] = $depth;						array_push($ReturnObject, $F);			}		}		$app->response()->header("Content-Type", "application/json");		echo format_json(json_encode($ReturnObject));	});$route = '/ongithub/products/:id';$app->get($route, function ($IncomingID)  use ($app){	$ObjectText = file_get_contents('https://raw.github.com/ongithub/products/gh-pages/products.json');	$ObjectResult = json_decode($ObjectText,true);	$ReturnObject = array();	foreach($ObjectResult as $Object){		$IncludeRecord = 0;		$id = $Object['id'];		$name = $Object['name'];		$description = $Object['description'];		$url = $Object['url'];		$image = $Object['image'];		$thumbnailUrl = $Object['thumbnailUrl'];		$keywords = $Object['keywords'];		$brand = $Object['brand'];		$color = $Object['color'];				$itemCondition = $Object['itemCondition'];		$manufacturer = $Object['manufacturer'];		$model = $Object['model'];		$sku = $Object['sku'];		$weight = $Object['weight'];		$width = $Object['width'];		$height = $Object['height'];		$depth = $Object['depth'];		if($IncomingID==$id)			{			$IncludeRecord=1;			}		if($IncludeRecord==1)			{			$F = array();			$F['id'] = $id;			$F['name'] = $name;			$F['description'] = $description;			$F['url'] = $url;			$F['image'] = $image;			$F['thumbnailUrl'] = $thumbnailUrl;			$F['keywords'] = $keywords;			$F['brand'] = $brand;			$F['color'] = $color;			$F['itemCondition'] = $itemCondition;			$F['manufacturer'] = $manufacturer;			$F['model'] = $model;			$F['sku'] = $sku;			$F['weight'] = $weight;			$F['width'] = $width;			$F['height'] = $height;			$F['depth'] = $depth;			array_push($ReturnObject, $F);			}		}		$app->response()->header("Content-Type", "application/json");		echo format_json(json_encode($ReturnObject));	});?>